import React, { useRef, useEffect } from 'react';
import { Box, Grid, Typography, Button, useTheme } from '@mui/material';
import { gsap } from 'gsap';
import { motion } from 'framer-motion';

function AboutSection() {
  const theme = useTheme();
  const sectionRef = useRef();

  useEffect(() => {
    gsap.fromTo(sectionRef.current, { opacity: 0 }, { opacity: 1, duration: 1 });
  }, []);

  return (
    <Box ref={sectionRef} sx={{ padding: theme.spacing(8, 0) }}>
      <Typography variant="h2" gutterBottom>
        {/* Content */}
      </Typography>
      <Button variant="contained" href="/about">
        {/* Content */}
      </Button>
      <Grid container spacing={2}>
        <Grid item xs={12} sm={6} md={3}>
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
          >
            {/* Video or content here */}
          </motion.div>
        </Grid>
        {/* This will take the full width on extra-small to small screens and half width on medium to larger screens */}
        <Grid item xs={12} md={6}>
          <Box sx={{ height: '400px', bgcolor: 'primary.main' }}>
            {/* Content */}
          </Box>
        </Grid>
        <Grid item xs={12} md={6}>
          {/* Same as above for symmetry */}
          <Box sx={{ height: '400px', bgcolor: 'primary.light' }}>
            {/* Content */}
          </Box>
        </Grid>
        {/* You can add more Grid items here */}
      </Grid>
    </Box>
  );
}

export default AboutSection;
