import React, { useRef, useEffect } from 'react';
import { Box, Typography, Link, useTheme } from '@mui/material';
import { gsap } from 'gsap';

function Footer() {
  const theme = useTheme();
  const footerRef = useRef();

  useEffect(() => {
    gsap.fromTo(footerRef.current, { y: 50, opacity: 0 }, { y: 0, opacity: 1, duration: 1 });
  }, []);

  // Styles for the footer box
  const footerStyles = {
    padding: theme.spacing(10, 2),
    backgroundColor: theme.palette.grey[100],
    textAlign: 'center',
    position: 'relative',
  };

  // Styles for the links
  const linkStyle = {
    margin: theme.spacing(2),
    fontWeight: '500',
    color: theme.palette.primary.main,
    '&:hover': {
      textDecoration: 'underline',
    },
  };

  return (
    <Box ref={footerRef} sx={footerStyles}>
      <Typography variant="h6">Question about my work?</Typography>
      <Link href="mailto:your.email@example.com" sx={linkStyle}>
        Let's chat.
      </Link>
      {/* Add additional content and links as needed */}
    </Box>
  );
}

export default Footer;
