import React, { useRef, useEffect } from 'react';
import { Box, Grid, Typography, Paper, useMediaQuery, useTheme } from '@mui/material';
import { gsap } from 'gsap';

function AboutSection() {
  const theme = useTheme();
  const sectionRef = useRef();

  useEffect(() => {
    gsap.fromTo(sectionRef.current, { opacity: 0 }, { opacity: 1, duration: 1 });
  }, []);

  const boxStyle = {
    height: '500px', // adjust this as needed
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: theme.shape.borderRadius,
  };

  return (
    <Box ref={sectionRef} sx={{ maxWidth: 'lg', margin: 'auto', padding: theme.spacing(8, 2) }}>
      <Grid container spacing={2}>
        {/* Full width grid item */}
        <Grid item xs={12}>
          <Paper elevation={6} sx={{ ...boxStyle, bgcolor: 'primary.main' }}>
            {/* Content for the large box */}
          </Paper>
        </Grid>
        {/* Two grid items in the second row */}
        <Grid item xs={12} md={6}>
          <Paper elevation={6} sx={{ ...boxStyle, bgcolor: 'primary.light' }}>
            {/* Content for the first middle box */}
          </Paper>
        </Grid>
        <Grid item xs={12} md={6}>
          <Paper elevation={6} sx={{ ...boxStyle, bgcolor: 'secondary.main' }}>
            {/* Content for the second middle box */}
          </Paper>
        </Grid>
        {/* Two grid items in the third row */}
        <Grid item xs={12} md={6}>
          <Paper elevation={6} sx={{ ...boxStyle, bgcolor: 'secondary.light' }}>
            {/* Content for the third box (below the first middle box) */}
          </Paper>
        </Grid>
        <Grid item xs={12} md={6}>
          <Paper elevation={6} sx={{ ...boxStyle, bgcolor: 'error.main' }}>
            {/* Content for the fourth box (below the second middle box) */}
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
}

export default AboutSection;
