import React, { useRef, useEffect } from 'react';
import { Box, Grid, Paper, useTheme } from '@mui/material';
import { gsap } from 'gsap';

function AboutSection() {
  const theme = useTheme();
  const sectionRef = useRef();

  useEffect(() => {
    gsap.fromTo(sectionRef.current, { opacity: 0 }, { opacity: 1, duration: 1 });
  }, []);

  const boxStyle = (bgColor) => ({
    height: '800px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: theme.shape.borderRadius,
    bgcolor: theme.palette[bgColor].main, // Assuming you have these colors defined in your theme
  });

  return (
    <Box ref={sectionRef} sx={{ maxWidth: '90%', margin: 'auto', padding: theme.spacing(8, 2) }}>
      <Grid container spacing={2}>
        {/* Full width grid item */}
        <Grid item xs={12}>
          <Paper elevation={6} sx={boxStyle('primary')}>
            {/* Content for the large box */}
          </Paper>
        </Grid>
        {/* Two grid items in one row */}
        <Grid item xs={12} sm={6}>
          <Paper elevation={6} sx={boxStyle('secondary')}>
            {/* Content for the first middle box */}
          </Paper>
        </Grid>
        <Grid item xs={12} sm={6}>
          <Paper elevation={6} sx={boxStyle('error')}>
            {/* Content for the second middle box */}
          </Paper>
        </Grid>
        {/* Two grid items in another row */}
        <Grid item xs={12} sm={6}>
          <Paper elevation={6} sx={boxStyle('info')}>
            {/* Content for the third box */}
          </Paper>
        </Grid>
        <Grid item xs={12} sm={6}>
          <Paper elevation={6} sx={boxStyle('warning')}>
            {/* Content for the fourth box */}
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
}

export default AboutSection;
