import React, { useRef, useEffect } from 'react';
import { Box, Typography, Link, Grid, Paper, useTheme } from '@mui/material';
import { gsap } from 'gsap';
import { motion } from 'framer-motion';

function Footer() {
  const theme = useTheme();
  const footerRef = useRef();

  useEffect(() => {
    gsap.fromTo(footerRef.current, { y: 50, opacity: 0 }, { y: 0, opacity: 1, duration: 1 });
  }, []);

  return (
    <Box ref={footerRef} sx={{
      padding: theme.spacing(10, 2),
      backgroundColor: theme.palette.grey[100],
      textAlign: 'center',
      position: 'relative',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
    }}>
      <Box sx={{ position: 'absolute', bottom: theme.spacing(2), width: '100%', display: 'flex', justifyContent: 'center' }}>
        {/* Social Links */}
        <Link href="https://twitter.com" sx={{ margin: theme.spacing(1) }}>Twitter</Link>
        <Link href="https://github.com" sx={{ margin: theme.spacing(1) }}>GitHub</Link>
        <Link href="https://linkedin.com" sx={{ margin: theme.spacing(1) }}>LinkedIn</Link>
        <Link href="https://dribbble.com" sx={{ margin: theme.spacing(1) }}>Dribbble</Link>
      </Box>
    </Box>
  );
}

export default Footer;
