// Footer.js
import React, { useRef, useEffect } from 'react';
import { Box, Typography, Link, useTheme } from '@mui/material';
import { gsap } from 'gsap';

function Footer() {
  const theme = useTheme();
  const footerRef = useRef();

  useEffect(() => {
    gsap.fromTo(footerRef.current, { y: 50, opacity: 0 }, { y: 0, opacity: 1, duration: 1 });
  }, []);

  return (
    <Box ref={footerRef} sx={{
      padding: theme.spacing(10, 2),
      backgroundColor: theme.palette.background.default, // Set to the default background of the page
      textAlign: 'center',
      position: 'relative',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      width: '100%', // Ensure it spans the full width
    }}>
      <Typography variant="h6" sx={{ mb: theme.spacing(2) }}>
        Question about my work?
      </Typography>
      <Link href="mailto:your.email@example.com" sx={{ mb: theme.spacing(2), textDecoration: 'none', '&:hover': { textDecoration: 'underline' } }}>
        Let's chat.
      </Link>
      {/* Social Links */}
      {/* ... */}
    </Box>
  );
}

export default Footer;
