import React, { useRef, useEffect } from 'react';
import { Box, Grid, Typography, Button, useMediaQuery, useTheme } from '@mui/material';
import { gsap } from 'gsap';

function AboutSection() {
  const theme = useTheme();
  const isLargeScreen = useMediaQuery(theme.breakpoints.up('md'));
  const sectionRef = useRef();

  useEffect(() => {
    gsap.fromTo(sectionRef.current, { opacity: 0 }, { opacity: 1, duration: 1 });
  }, []);

  // Use `theme.spacing()` to handle consistent spacing and responsive margins
  const gridSpacing = isLargeScreen ? theme.spacing(3) : theme.spacing(2);

  return (
    <Box ref={sectionRef} sx={{ padding: isLargeScreen ? '0 0 240px' : '0 0 60px' }}>
      <Typography variant="h2" gutterBottom>
        Besides design, I love writing music, programming, games and taking my dog to his favorite parks.
      </Typography>
      <Button variant="contained" href="/about">
        About Me
      </Button>
      <Grid container spacing={gridSpacing}>
        <Grid item xs={12} md={6} className="first">
          {/* Video or content here */}
        </Grid>
        <Grid item xs={12} md={6} className="second">
          {/* Video or content here */}
        </Grid>
        {/* Add more <Grid> items as needed */}
      </Grid>
    </Box>
  );
}

export default AboutSection;
