import React, { useRef, useEffect } from 'react';
import { Box, Typography, Link, Grid, Paper, useTheme } from '@mui/material';
import { gsap } from 'gsap';

function Footer() {
  const theme = useTheme();
  const footerRef = useRef();

  useEffect(() => {
    gsap.fromTo(footerRef.current, { y: 50, opacity: 0 }, { y: 0, opacity: 1, duration: 1 });
  }, []);

  const footerStyles = {
    padding: theme.spacing(10, 2),
    backgroundColor: theme.palette.grey[100], // very light grey color
    borderRadius: theme.shape.borderRadius,
    textAlign: 'center',
    position: 'relative', // For positioning any children absolutely within the footer
    boxShadow: theme.shadows[2], // subtle shadow
  };

  const linkStyle = {
    margin: theme.spacing(2),
    fontWeight: '500',
    color: theme.palette.primary.main, // Adjust color as needed
    textDecoration: 'none',
    '&:hover': {
      textDecoration: 'underline',
    },
  };

  return (
    <Box ref={footerRef} sx={footerStyles}>
      <Typography variant="h6">
        Question about my work?
      </Typography>
      <Link href="https://www.linkedin.com" sx={linkStyle}>
        Let's chat.
      </Link>
      <Box sx={{ position: 'absolute', bottom: theme.spacing(2), width: '100%' }}>
        <Typography variant="body2">
          View this portfolio on Figma or Github.
        </Typography>
        {/* Add social links here */}
      </Box>
    </Box>
  );
}

export default Footer;
