import React, { useRef, useEffect } from 'react';
import { gsap } from 'gsap';
import { Box, Grid, Typography, Button, useMediaQuery, useTheme } from '@mui/material';
import ArrowDownwardIcon from '@mui/icons-material/ArrowDownward';
import AboutSection from './components/AboutSection';

function App() {
  const theme = useTheme();
  const isLargeScreen = useMediaQuery(theme.breakpoints.up('md'));
  const appRef = useRef();

  useEffect(() => {
    gsap.fromTo(appRef.current, { opacity: 0 }, { opacity: 1, duration: 1 });
  }, []);

  return (
    <Box ref={appRef} className="App">
      <AboutSection />
      <Grid container direction="column" justifyContent="center" alignItems="center" style={{ height: '100vh' }}>
        <Typography variant="h4" gutterBottom style={{ color: 'white', opacity: 0.75 }}>
          Hello, I'm Diana.
        </Typography>
        <Typography variant="h5" style={{ color: 'white', opacity: 0.85 }}>
          Full Stack Web Developer
        </Typography>
        <Button 
          variant="contained" 
          endIcon={<ArrowDownwardIcon />}
          style={{ marginTop: theme.spacing(2), opacity: 0.95 }}
        >
          View my work
        </Button>
      </Grid>
    </Box>
  );
}

export default App;
