import React, { useRef, useEffect } from 'react';
import { Box, Grid, Paper, useTheme, Typography, Link } from '@mui/material';
import { gsap } from 'gsap';

function AboutSection() {
  const theme = useTheme();
  const sectionRef = useRef();

  useEffect(() => {
    gsap.fromTo(sectionRef.current, { opacity: 0 }, { opacity: 1, duration: 1 });
  }, []);

  const colors = {
    // Assuming these are the colors you want to use, replace with actual hex codes if needed
    firstBox: '#FFCDD2', // Light pink
    secondBox: '#F8BBD0', // Pink
    thirdBox: '#E1BEE7', // Purple
    fourthBox: '#D1C4E9', // Light purple
    fifthBox: '#C5CAE9', // Light blue
    sixthBox: '#F5F5F5',// Light blue
  };

  const boxStyle = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: theme.shape.borderRadius,
    boxShadow: theme.shadows[2],
    height: 'auto', // Auto height based on content
    padding: theme.spacing(4), // Inner padding for the content
    bgcolor: colors.sixthBox, // The color for the sixth box
  };

  // Corrected styles for text and link
  const questionBoxStyle = {
    display: 'flex', 
    flexDirection: 'column', 
    alignItems: 'center', 
    justifyContent: 'center',
    height: '100%', // Take the full height of the parent
  };

  const linkStyle = {
    marginTop: theme.spacing(2), 
    fontWeight: '500', 
    color: theme.palette.primary.main,
    textDecoration: 'none',
    '&:hover': {
      textDecoration: 'underline',
    },
  };

  return (
    <Box ref={sectionRef} sx={{ maxWidth: '78%', margin: 'auto', padding: theme.spacing(8, 2) }}>
      <Grid container spacing={2}>
        {/* Large box */}
        <Grid item xs={12}>
          <Paper elevation={6} sx={{ ...boxStyle, bgcolor: colors.firstBox }}>
            {/* Content for the large box */}
          </Paper>
        </Grid>
        {/* First row of two boxes */}
        <Grid item xs={12} md={6}>
          <Paper elevation={6} sx={{ ...boxStyle, bgcolor: colors.secondBox }}>
            {/* Content for the first middle box */}
          </Paper>
        </Grid>
        <Grid item xs={12} md={6}>
          <Paper elevation={6} sx={{ ...boxStyle, bgcolor: colors.thirdBox }}>
            {/* Content for the second middle box */}
          </Paper>
        </Grid>
        {/* Second row of two boxes */}
        <Grid item xs={12} md={6}>
          <Paper elevation={6} sx={{ ...boxStyle, bgcolor: colors.fourthBox }}>
            {/* Content for the third box */}
          </Paper>
        </Grid>
        <Grid item xs={12} md={6}>
          <Paper elevation={6} sx={{ ...boxStyle, bgcolor: colors.fifthBox }}>
            {/* Content for the fourth box */}
          </Paper>
        </Grid>
        </Grid>
      <Grid container spacing={40} sx={{ marginTop: theme.spacing(10) }}>
        <Grid item xs={12}>
          <Paper elevation={6} sx={boxStyle}>
            <Box sx={questionBoxStyle}>
              <Typography variant="h6">
                Question about my work?
              </Typography>
              <Link href="mailto:dianalophernandez@gmail.com" sx={linkStyle}>
                Let's connect.
              </Link>
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
}

export default AboutSection;
