import React, { useState, useEffect } from 'react';
import { motion, useAnimation } from 'framer-motion';
import { useTheme } from '@mui/material/styles';
import { gsap } from 'gsap';

const Cursor = () => {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const controls = useAnimation();
  const theme = useTheme(); // Using theme from Material-UI for styles

  useEffect(() => {
    const mouseMoveHandler = (event) => {
      setPosition({ x: event.clientX, y: event.clientY });
    };

    const mouseEnterHandler = () => {
      controls.start('hover');
    };

    const mouseLeaveHandler = () => {
      controls.start('default');
    };

    window.addEventListener('mousemove', mouseMoveHandler);
    window.addEventListener('mouseenter', mouseEnterHandler);
    window.addEventListener('mouseleave', mouseLeaveHandler);

    return () => {
      window.removeEventListener('mousemove', mouseMoveHandler);
      window.removeEventListener('mouseenter', mouseEnterHandler);
      window.removeEventListener('mouseleave', mouseLeaveHandler);
    };
  }, [controls]);

  useEffect(() => {
    // Example of GSAP animation that can be triggered on a specific action
    gsap.to(controls, {
      duration: 0.2,
      ease: 'power3.inOut',
      opacity: 1,
      scale: 1.5,
      backgroundColor: theme.palette.info.light,
    });
  }, [controls, theme.palette.info.light]);

  const cursorVariants = {
    default: {
      x: position.x - 16,
      y: position.y - 16,
      backgroundColor: theme.palette.info.main,
      boxShadow: `0 0 10px ${theme.palette.info.main}`,
      scale: 1,
      opacity: 0.7,
    },
    hover: {
      scale: 1.5,
      opacity: 1,
      backgroundColor: theme.palette.info.light,
    },
  };

  return (
    <motion.div
      variants={cursorVariants}
      animate={controls}
      initial="default"
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '32px',
        height: '32px',
        borderRadius: '50%',
        zIndex: 9999,
        pointerEvents: 'none',
      }}
    />
  );
};

export default Cursor;
