import React, { useRef, useEffect } from 'react';
import { Box, Grid, Typography, Paper, useMediaQuery, useTheme } from '@mui/material';
import { gsap } from 'gsap';

function AboutSection() {
  const theme = useTheme();
  const isLargeScreen = useMediaQuery(theme.breakpoints.up('md'));
  const sectionRef = useRef();

  useEffect(() => {
    gsap.fromTo(sectionRef.current, { opacity: 0 }, { opacity: 1, duration: 1 });
  }, []);

  const boxStyle = {
    height: '400px', // adjust this as needed
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: theme.shape.borderRadius,
  };

  return (
    <Box ref={sectionRef} sx={{ maxWidth: 'lg', margin: 'auto', padding: theme.spacing(8, 2) }}>
      <Grid container spacing={2}>
        {/* Full width grid item */}
        <Grid item xs={12}>
          <Paper elevation={6} sx={{ ...boxStyle, bgcolor: 'primary.main' }}>
            {/* Content */}
          </Paper>
        </Grid>
        {/* Smaller grid items */}
        {isLargeScreen && (
          <>
            <Grid item xs={12} sm={6} md={4}>
              <Paper elevation={6} sx={{ ...boxStyle, bgcolor: 'primary.light' }}>
                {/* Content */}
              </Paper>
            </Grid>
            <Grid item xs={12} sm={6} md={4}>
              <Paper elevation={6} sx={{ ...boxStyle, bgcolor: 'secondary.main' }}>
                {/* Content */}
              </Paper>
            </Grid>
            <Grid item xs={12} sm={6} md={4}>
              <Paper elevation={6} sx={{ ...boxStyle, bgcolor: 'secondary.light' }}>
                {/* Content */}
              </Paper>
            </Grid>
          </>
        )}
        {/* Continue with other items as needed, adjusting xs, sm, md as per your design */}
      </Grid>
    </Box>
  );
}

export default AboutSection;
