import React, { useRef, useEffect } from 'react';
import { Box, Typography, Link, useTheme } from '@mui/material';
import { gsap } from 'gsap';

function Footer() {
  const theme = useTheme();
  const footerRef = useRef();

  useEffect(() => {
    gsap.fromTo(footerRef.current, { y: 50, opacity: 0 }, { y: 0, opacity: 1, duration: 1 });
  }, []);

  return (
    <Box
      ref={footerRef}
      sx={{
        width: '100%',
        padding: theme.spacing(8, 2),
        textAlign: 'center',
        backgroundColor: theme.palette.grey[100], // Or any color you prefer
      }}
    >
      <Typography variant="h6">
        Question about my work?
      </Typography>
      <Link href="mailto:your.email@example.com" sx={{ display: 'block', margin: theme.spacing(2) }}>
        Let's chat.
      </Link>
      {/* Add social links or other footer content here */}
    </Box>
  );
}

export default Footer;
