import React, { useRef, useEffect } from 'react';
import { Box, Grid, Paper, useTheme } from '@mui/material';
import { gsap } from 'gsap';

function AboutSection() {
  const theme = useTheme();
  const sectionRef = useRef();

  useEffect(() => {
    gsap.fromTo(sectionRef.current, { opacity: 0 }, { opacity: 1, duration: 1 });
  }, []);

  // Define your color palette based on the CSS classes
  const colors = {
    primaryMain: '#F5E6CC',  // Replace with actual color codes
    primaryLight: '#FFDDE2', // Replace with actual color codes
    secondaryMain: '#D5ECC2', // Replace with actual color codes
    secondaryLight: '#98DDCA', // Replace with actual color codes
    errorMain: '#FFAAA7' // Replace with actual color codes
  };

  const boxStyle = (color) => ({
    height: '800px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: theme.shape.borderRadius,
    bgcolor: color,
  });

  return (
    <Box ref={sectionRef} sx={{ maxWidth: '100%', margin: 'auto', padding: theme.spacing(8, 2) }}>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <Paper elevation={6} sx={boxStyle(colors.primaryMain)}>
            {/* Content for the large box */}
          </Paper>
        </Grid>
        <Grid item xs={12} md={6}>
          <Paper elevation={6} sx={boxStyle(colors.primaryLight)}>
            {/* Content for the first middle box */}
          </Paper>
        </Grid>
        <Grid item xs={12} md={6}>
          <Paper elevation={6} sx={boxStyle(colors.secondaryMain)}>
            {/* Content for the second middle box */}
          </Paper>
        </Grid>
        <Grid item xs={12} md={6}>
          <Paper elevation={6} sx={boxStyle(colors.secondaryLight)}>
            {/* Content for the third box (below the first middle box) */}
          </Paper>
        </Grid>
        <Grid item xs={12} md={6}>
          <Paper elevation={6} sx={boxStyle(colors.errorMain)}>
            {/* Content for the fourth box (below the second middle box) */}
          </Paper>
        </Grid>
      </Grid>

    <Grid container spacing={2} sx={{ marginTop: theme.spacing(4) }}> {/* Adjust the margin as needed */}
        <Grid item xs={12}>
          <Paper elevation={6} sx={{ ...boxStyle, bgcolor: 'info.main' }}> {/* Change the bgcolor to a different one if needed */}
            {/* Content for the second large box */}
          </Paper>
        </Grid>
      </Grid>

    </Box>
  );
}

export default AboutSection;
