import React, { useRef, useEffect } from 'react';
import { Box, Typography, Link, useTheme, Paper, Grid } from '@mui/material';
import { gsap } from 'gsap';
import { motion } from 'framer-motion';

function Footer() {
  const theme = useTheme();
  const footerRef = useRef();

  useEffect(() => {
    gsap.fromTo(footerRef.current, { y: 50, opacity: 0 }, { y: 0, opacity: 1, duration: 1 });
  }, []);

  // Styles for the footer box
  const footerStyles = {
    padding: theme.spacing(10, 2),
    backgroundColor: theme.palette.grey[100],
    textAlign: 'center',
    position: 'relative',
  };


  return (
    <Box ref={footerRef} sx={footerStyles}>
      <Typography variant="h6">Question about my work?</Typography>
      <Link href="mailto:your.email@example.com" sx={linkStyle}>
        Let's chat.
      </Link>
      <Box sx={{ position: 'absolute', bottom: theme.spacing(2), width: '100%', display: 'flex', justifyContent: 'center' }}>
        <Link href="https://twitter.com" sx={socialLinkStyle}>Twitter</Link>
        <Link href="https://github.com" sx={socialLinkStyle}>GitHub</Link>
        <Link href="https://linkedin.com" sx={socialLinkStyle}>LinkedIn</Link>
        <Link href="https://dribbble.com" sx={socialLinkStyle}>Dribbble</Link>
      </Box>
    </Box>
  );
}

export default Footer;
